import React from 'react';
import siteContent from '../data/siteContent';

const HeroSection = ({ scrollToSection, handleButtonAction }) => {
  return (
    <section 
      id="home" 
      className="relative min-h-screen pt-20 bg-cover bg-center flex items-center justify-center"
      style={{ backgroundImage: `url(${siteContent.hero.background})` }}
    >
      <div className="absolute inset-0 bg-black opacity-50"></div>
      <div className="relative z-10 text-center px-4">
        {/* Logo */}
        <div className="mb-8 flex justify-center">
          <img 
            src={siteContent.hero.logo} 
            alt={siteContent.navigation.logo.alt} 
            className="w-32 h-32 rounded-full border-4 border-yellow-500"
          />
        </div>

        <h1 className="text-5xl font-bold mb-6">{siteContent.hero.title}</h1>
        <p className="text-xl mb-8 max-w-2xl mx-auto">
          {siteContent.hero.subtitle}
        </p>

        {/* Social Media Links with Usernames */}
        <div className="flex justify-center space-x-8 mb-8">
          {siteContent.hero.social.map((platform, index) => (
            <a 
              key={index}
              href={platform.url} 
              target="_blank" 
              rel="noopener noreferrer" 
              className="flex items-center text-white hover:text-yellow-400 transition-colors"
            >
              <img 
                src={platform.iconPath} 
                alt={platform.platform} 
                className="w-6 h-6 mr-2" 
              />
              <span>{platform.username}</span>
            </a>
          ))}
        </div>

        <div className="flex flex-wrap justify-center gap-4">
          {siteContent.hero.buttons.map((button, index) => (
            <button 
              key={index}
              onClick={() => handleButtonAction(button)}
              className={getButtonClass(button.type)}
            >
              {button.text}
            </button>
          ))}
        </div>
      </div>
    </section>
  );
};

// Helper function for button styling
const getButtonClass = (type) => {
  switch(type) {
    case 'primary':
      return "px-6 py-3 bg-yellow-500 text-gray-900 font-bold rounded-full";
    case 'secondary':
      return "px-6 py-3 border-2 border-white text-white rounded-full";
    case 'outlined':
      return "px-6 py-3 border-2 border-yellow-500 text-yellow-500 font-bold rounded-full";
    default:
      return "px-6 py-3 bg-gray-800 text-white rounded-full";
  }
};

export default HeroSection;
