import React from 'react';
import siteContent from '../data/siteContent';

const PhotosSection = ({ handleButtonAction }) => {
  return (
    <section 
      id="photos" 
      className="min-h-screen py-20 bg-cover bg-center"
      style={{ 
        backgroundImage: `linear-gradient(rgba(0,0,0,0.8), rgba(0,0,0,0.8)), url(${siteContent.photos.background})` 
      }}
    >
      <div className="container mx-auto px-4">
        <div className="text-center mb-12">
          <h2 className="text-4xl font-bold mb-4">{siteContent.photos.title}</h2>
          <p className="text-gray-300 max-w-2xl mx-auto">
            {siteContent.photos.subtitle}
          </p>
        </div>

        {/* Masonry Layout Photo Grid */}
        <div className="columns-2 gap-6">
          {siteContent.photos.gallery.map((photo, index) => (
            <div key={index} className="mb-6 overflow-hidden rounded-lg group relative">
              <img 
                src={photo.path}
                alt={photo.alt}
                className="w-full h-auto object-cover transition-transform duration-500 group-hover:scale-110"
              />
              <div className="absolute inset-0 bg-black opacity-0 group-hover:opacity-60 transition-opacity duration-300"></div>
              <div className="absolute inset-0 flex items-end opacity-0 group-hover:opacity-100 transition-opacity duration-300 p-6">
                <div>
                  <h3 className="text-xl font-bold">{photo.location}</h3>
                  <p className="text-gray-300">{photo.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="text-center mt-12">
          {siteContent.photos.buttons.map((button, index) => (
            <button 
              key={index}
              onClick={() => handleButtonAction(button)}
              className={getButtonClass(button.type)}
            >
              {button.text}
            </button>
          ))}
        </div>
      </div>
    </section>
  );
};

// Helper function for button styling
const getButtonClass = (type) => {
  switch(type) {
    case 'primary':
      return "px-6 py-3 bg-yellow-500 text-gray-900 font-bold rounded-full";
    case 'secondary':
      return "px-6 py-3 border-2 border-white text-white rounded-full";
    case 'outlined':
      return "px-6 py-3 border-2 border-yellow-500 text-yellow-500 font-bold rounded-full";
    default:
      return "px-6 py-3 bg-gray-800 text-white rounded-full";
  }
};

export default PhotosSection;
