import React from 'react';
import StoryExpander from './StoryExpander';
import siteContent from '../data/siteContent';

const StoriesSection = () => {
  return (
    <section 
      id="stories" 
      className="min-h-screen py-20 bg-cover bg-center"
      style={{ 
        backgroundImage: `linear-gradient(rgba(0,0,0,0.7), rgba(0,0,0,0.7)), url(${siteContent.stories.background})` 
      }}
    >
      <div className="container mx-auto px-4">
        <div className="text-center mb-12">
          <h2 className="text-4xl font-bold mb-4">{siteContent.stories.title}</h2>
          <p className="text-gray-300 max-w-2xl mx-auto">
            {siteContent.stories.subtitle}
          </p>
        </div>

        {/* Alternating Stories Layout with Row Format */}
        <div className="space-y-8 max-w-5xl mx-auto">
          {siteContent.stories.articles.map((story, index) => (
            <div key={index} className="bg-black bg-opacity-40 rounded-xl overflow-hidden">
              <div className={`flex ${index % 2 !== 0 ? 'flex-row-reverse' : 'flex-row'}`}>
                {/* Image Side */}
                <div className="w-1/4 p-4 flex items-center">
                  <img
                    src={story.image}
                    alt={story.alt}
                    className="w-full rounded-lg object-cover"
                  />
                </div>

                {/* Content Side */}
                <div className="w-3/4 p-6 flex flex-col justify-center">
                  <div>
                    <span className="inline-block px-3 py-1 bg-yellow-500 text-xs font-bold text-gray-900 rounded-full mb-3">
                      {story.category}
                    </span>
                    <h3 className="text-xl font-bold mb-3">{story.title}</h3>
                    <p className="text-gray-300 mb-4">{story.excerpt}</p>
                    <StoryExpander story={story} />
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default StoriesSection;
