import React from 'react';
import siteContent from '../data/siteContent';

const AboutSection = () => {
  return (
    <section 
      id="about" 
      className="min-h-screen py-20 bg-cover bg-center"
      style={{ 
        backgroundImage: `linear-gradient(rgba(0,0,0,0.7), rgba(0,0,0,0.7)), url(${siteContent.about.background})` 
      }}
    >
      <div className="container mx-auto px-4">
        <div className="text-center mb-12">
          <h2 className="text-4xl font-bold mb-4">{siteContent.about.title}</h2>
          <p className="text-gray-300 max-w-2xl mx-auto">
            {siteContent.about.subtitle}
          </p>
        </div>

        <div className="bg-black bg-opacity-50 rounded-2xl overflow-hidden max-w-4xl mx-auto">
          <div className="flex flex-col md:flex-row items-center">
            <div className="md:w-1/4 p-4">
              <img
                src={siteContent.about.profile.image}
                alt={siteContent.about.profile.name}
                className="w-full rounded-lg object-cover"
              />
            </div>

            <div className="md:w-3/4 p-8">
              <h3 className="text-2xl font-bold mb-6">Hello, I'm {siteContent.about.profile.name}</h3>
              {siteContent.about.profile.bio.map((paragraph, index) => (
                <p key={index} className="text-gray-300 mb-6">{paragraph}</p>
              ))}

              {/* Social Media Links with Usernames */}
              <div className="flex justify-center space-x-8 mb-6">
                {siteContent.about.profile.social.map((platform, index) => (
                  <a 
                    key={index}
                    href={platform.url} 
                    className="flex items-center text-yellow-400 hover:text-yellow-300"
                  >
                    <img 
                      src={platform.iconPath} 
                      alt={platform.platform} 
                      className="w-5 h-5 mr-2" 
                    />
                    <span>{platform.username}</span>
                  </a>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default AboutSection;
