import React, { useState } from 'react';
import { MessageSquare, Instagram, Video, ChevronDown, ChevronUp } from 'lucide-react';
import siteContent from '../data/siteContent';

// Story Expander Component 
const StoryExpander = ({ story }) => {
  const [expanded, setExpanded] = useState(false);

  return (
    <>
      {expanded ? (
        <>
          <div style={{ color: '#d1d5db', marginBottom: '16px' }}>
            {story.fullStory.split('\n\n').map((paragraph, i) => (
              <p key={i} style={{ marginBottom: '16px' }}>{paragraph}</p>
            ))}
          </div>
          <button 
            onClick={() => setExpanded(false)} 
            style={{ 
              display: 'flex', 
              alignItems: 'center', 
              color: '#eab308', 
              fontWeight: '500', 
              background: 'none', 
