import React, { useState } from 'react';
import { ChevronDown, ChevronUp } from 'lucide-react';

const StoryExpander = ({ story }) => {
  const [expanded, setExpanded] = useState(false);

  return (
    <>
      {expanded ? (
        <>
          <div className="text-gray-300 mb-4 story-content">
            {story.fullStory.split('\n\n').map((paragraph, i) => (
              <p key={i} className="mb-4">{paragraph}</p>
            ))}
          </div>
          <button 
            onClick={() => setExpanded(false)} 
            className="flex items-center text-yellow-400 font-medium hover:text-yellow-300"
          >
            Show Less <ChevronUp size={18} className="ml-1" />
          </button>
        </>
      ) : (
        <button 
          onClick={() => setExpanded(true)} 
          className="flex items-center text-yellow-400 font-medium hover:text-yellow-300"
        >
          Read More <ChevronDown size={18} className="ml-1" />
        </button>
      )}
    </>
  );
};

export default StoryExpander;
