import React from 'react';
import siteContent from '../data/siteContent';

const Navigation = ({ activeSection, setActiveSection, scrollToSection }) => {
  return (
    <nav className="fixed top-0 left-0 right-0 bg-black bg-opacity-80 z-50">
      <div className="container mx-auto px-4 py-4 flex flex-wrap justify-between items-center">
        <div className="flex items-center">
          <div className="w-10 h-10 bg-yellow-500 rounded-full overflow-hidden">
            <img 
              src={siteContent.navigation.logo.path} 
              alt={siteContent.navigation.logo.alt} 
              className="w-full h-full object-cover" 
            />
          </div>
          <h1 className="ml-3 text-xl font-bold">{siteContent.meta.title}</h1>
        </div>

        <div className="flex space-x-6">
          {siteContent.navigation.links.map(item => (
            <button
              key={item.id}
              className={`px-3 py-2 ${activeSection === item.id ? 'text-yellow-500' : 'text-white'}`}
              onClick={() => scrollToSection(item.id)}
            >
              {item.label}
            </button>
          ))}
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
