import React, { useState } from 'react';
import { MessageSquare, Instagram, Video } from 'lucide-react';
import siteContent from '../data/siteContent';

// Import section components
import Navigation from './Navigation';
import HeroSection from './HeroSection';
import PhotoSection from './PhotoSection';
import StoriesSection from './StoriesSection';
import AboutSection from './AboutSection';

const ExpatTravellerWebsite = () => {
  const [activeSection, setActiveSection] = useState('home');

  const scrollToSection = (sectionId) => {
    document.getElementById(sectionId).scrollIntoView({ behavior: 'smooth' });
    setActiveSection(sectionId);
  };

  const handleButtonAction = (button) => {
    if (button.action === 'scroll') {
      scrollToSection(button.target);
    } else if (button.action === 'link') {
      window.open(button.target, '_blank');
    }
  };

  return (
    <div className="bg-gray-900 text-white font-sans">
      {/* Navigation */}
      <Navigation 
        activeSection={activeSection} 
        setActiveSection={setActiveSection} 
        scrollToSection={scrollToSection} 
      />

      {/* Hero Section */}
      <HeroSection 
        scrollToSection={scrollToSection} 
        handleButtonAction={handleButtonAction} 
      />

      {/* Photos Section */}
      <PhotoSection handleButtonAction={handleButtonAction} />

      {/* Stories Section */}
      <StoriesSection />

      {/* About Section */}
      <AboutSection />

      {/* Footer */}
      <footer className="bg-black py-10 text-gray-400">
        <div className="container mx-auto px-4 text-center">
          <div className="flex justify-center items-center mb-6">
            <div className="w-8 h-8 bg-yellow-500 rounded-full overflow-hidden">
              <img 
                src={siteContent.footer.logo} 
                alt={siteContent.meta.title} 
                className="w-full h-full object-cover"
              />
            </div>
            <h3 className="ml-3 text-lg font-bold text-white">{siteContent.meta.title}</h3>
          </div>

          <p className="mb-6">{siteContent.footer.tagline}</p>

          <div className="flex justify-center space-x-6 mb-8">
            {siteContent.footer.social.map((platform, index) => (
              <a 
                key={index}
                href={platform.url} 
                className="hover:text-white"
              >
                {platform.icon ? (
                  platform.icon === 'MessageSquare' ? <MessageSquare size={20} /> : 
                  platform.icon === 'Instagram' ? <Instagram size={20} /> : 
                  <Video size={20} />
                ) : (
                  <img 
                    src={platform.iconPath} 
                    alt={platform.platform} 
                    className="w-5 h-5" 
                  />
                )}
              </a>
            ))}
          </div>

          <p className="text-sm">{siteContent.footer.copyright}</p>
        </div>
      </footer>
    </div>
  );
};

export default ExpatTravellerWebsite;
